import React, { useState } from "react";
//import { connect } from "react-redux";
import styles from "./SearchResultsSummary.module.css";

export function SearchResultsSummary(props) {
  // const [price, setPrice] = useState("");

  // function submitFilter(e) {
  //   if (typeof props.searchWithFilter === "function") {
  //     props.searchWithFilter(props.term, props.location, price);
  //   }
  //   console.log(price);
  //   e.preventDefault();
  // }

  const [button, setButton] = useState(false);

  function handleButtonState(e) {
    setButton(true);
  }

  function handlePrice(priceParam) {
    props.searchWithFilter(props.term, props.location, priceParam);
  }

  function clearFilter(e) {
    if (typeof props.search === "function") {
      props.search(props.term, props.location);
    }
    e.preventDefault();
  }
  let resultStats = null;
  if (props.amountResults && props.shownResults) {
    resultStats = (
      <p>
        Showing 1-{props.shownResults} out of {props.amountResults} results{" "}
      </p>
    );
  }

  var btn_class = button ? "is-success" : "";

  return (
    <div className={styles.container}>
      <div className={styles["search-summary"]}>
        <h1 className="subtitle">
          <strong>{props.term}</strong> {props.location}
        </h1>
        {resultStats}
      </div>

      <div className={styles.filters}>
        <button className="button">
          <span className="icon">
            <i className="fas fa-sliders-h"></i>
          </span>
          <span>All filters</span>
        </button>

        <div className="buttons has-addons">
          <button
            className={`button ${btn_class} `}
            onClick={(e) => {
              handlePrice("1");
              handleButtonState(e);
            }}
            value="1"
          >
            $
          </button>
          <button
            className="button"
            onClick={(e) => {
              handlePrice("2");
            }}
            value="2"
          >
            $$
          </button>
          <button
            className="button"
            onClick={(e) => {
              handlePrice("3");
            }}
            value="3"
          >
            $$$
          </button>
          <button
            className="button"
            onClick={(e) => {
              handlePrice("4");
            }}
            value="4"
          >
            $$$$
          </button>
        </div>

        {/* <button className="button" onClick={submitFilter}>
          <span className="icon">
            <i className="fas fa-check"></i>
          </span>
          <span>Apply filter</span>
        </button> */}

        <button className="button" onClick={clearFilter}>
          <span className="icon">
            <i className="fas fa-minus-circle"></i>
          </span>
          <span>Clear filter</span>
        </button>
      </div>
    </div>
  );
}
