import React, { useState } from "react";
import styles from "./Pagination.module.css";

export function Pagination(props) {
  // this.state = {
  //   query: "",
  //   results: {},
  //   error: "",
  //   message: "",
  //   loading: false,
  //   totalResults: 0,
  //   totalPages: 0,
  //   currentPageNo: 0,
  // };

  // const [query, setQuery] = useState("");
  // const [results, setResults] = useState([]);
  // const [error, setError] = useState("");
  // const [message, setMessage] = useState("");
  // const [loading, setLoading] = useState(false);
  // const [totalResults, setTotalResults] = useState(0);
  // const [currentPage, setCurrentPage] = useState(0);

  const [offset, setOffset] = useState(21);
  const [button, setButton] = useState(false);

  function handleNextPage(e) {
    if (e.target.value === 1) {
      let value = offset + 20;
      props.handleNextPage(props.term, props.location, value);
      setOffset(value);
      setButton(true);
    } else if (e.target.value === 2) {
      let value = offset + 20;
      props.handleNextPage(props.term, props.location, value);
      setOffset(value);
      setButton(true);
    } else {
      let value = offset + 20;
      props.handleNextPage(props.term, props.location, value);
      setOffset(value);
      setButton(true);
    }
  }

  var btn_state = button ? "is-current" : "";

  return (
    <div className={styles["pagination"]}>
      <nav className="pagination" role="navigation" aria-label="pagination">
        <ul className="pagination-list">
          <li>
            <button
              className={`pagination-link ${btn_state}`}
              aria-label="Page 1"
              aria-current="page"
              value="1"
              onClick={(e) => handleNextPage}
            >
              1
            </button>
          </li>
          <li>
            <button
              className={`pagination-link ${btn_state}`}
              aria-label="Goto page 2"
              value="2"
              onClick={(e) => handleNextPage}
            >
              2
            </button>
          </li>
          <li>
            <button
              className={`pagination-link ${btn_state}`}
              aria-label="Goto page 3"
              value="3"
              onClick={(e) => handleNextPage}
            >
              3
            </button>
          </li>
        </ul>
        <div className="pagination-previous">Previous</div>
        <div className={`pagination-next ${styles["pagination-next"]}`}>
          Next page
        </div>
      </nav>
    </div>
  );
}
