import React, { useState } from "react";
import styles from "./SearchSuggestions.module.css";

export function SearchSuggestions(props) {
  let api = "https://fcc-weather-api.glitch.me/api/current?";
  const [state, setState] = useState({ isLoaded: false, items: {} });
  function fetchWeather(apiStr) {
    fetch(apiStr)
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          setState({
            isLoaded: true,
            items: result.main,
          });
          console.log(this.state);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setState({
            isLoaded: true,
            error,
          });
        }
      );
  }
  function searchDelivery() {
    const term = "Delivery";
    //const location = 'Buffalo';
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        api += `lat=${position.coords.latitude}&lon=${position.coords.longitude}`;
        fetchWeather(api);
      });
    }

    props.search(term, state);
  }

  return (
    <div className={styles.suggestions}>
      <span className="icon is-small">
        <i className="fas fa-utensils"></i>
      </span>
      <span className={styles.suggestion}>Restaurants</span>

      <span className="icon is-small">
        <i className="fas fa-cocktail"></i>
      </span>
      <span className={styles.suggestion}>Nightlife</span>

      <span className="icon is-small">
        <i className="fas fa-concierge-bell"></i>
      </span>
      <span className={styles.suggestion}>Service</span>

      <span className="icon is-small">
        <i className="fas fa-truck"></i>
      </span>
      <span className={styles.suggestion} onClick={() => searchDelivery()}>
        Delivery Services
      </span>
    </div>
  );
}
