import React, { useState } from "react";
//import { connect } from "react-redux";
import styles from "./SearchResultsSummary.module.css";

export function SearchResultsSummary(props) {
  const [price, setPrice] = useState(0);

  let resultStats = null;
  if (props.amountResults && props.shownResults) {
    resultStats = (
      <p>
        Showing 1-{props.shownResults} out of {props.amountResults} results{" "}
      </p>
    );
  }

  // function handlePriceFilter(e) {
  //   let priceType = e.target.value;
  //   if (e.target.clicked) {
  //     this.props.dispatch(addPriceFilter(priceType));
  //   } else {
  //     this.props.dispatch(removePriceFilter(priceType));
  //   }
  // }

  return (
    <div className={styles.container}>
      <div className={styles["search-summary"]}>
        <h1 className="subtitle">
          <strong>{props.term}</strong> {props.location}
        </h1>
        {resultStats}
      </div>

      <div className={styles.filters}>
        <button className="button">
          <span className="icon">
            <i className="fas fa-sliders-h"></i>
          </span>
          <span>All filters</span>
        </button>

        <div className="buttons has-addons">
          <button
            className="button"
            // onClick={(e) => handlePriceFilter(e)}
            value="$"
          >
            $
          </button>
          <button
            className="button"
            // onClick={(e) => handlePriceFilter(e)}
            value="$$"
          >
            $$
          </button>
          <button
            className="button"
            // onClick={(e) => handlePriceFilter(e)}
            value="$$$"
          >
            $$$
          </button>
          <button
            className="button"
            // onClick={(e) => this.handlePriceFilter(e)}
            value="$$$$"
          >
            $$$$
          </button>
        </div>

        <button className="button">
          <span className="icon">
            <i className="far fa-times-circle"></i>
          </span>
          <span>Clear filters</span>
        </button>

        {/* <button className='button'>
                    <span className='icon'><i className='fas fa-dollar-sign'></i></span>
                    <span>Cashback</span>
                </button> */}
      </div>
    </div>
  );
}
