import React from "react";
import { SearchResult } from "./SearchResult/SearchResult";
import useReactRouter from "use-react-router";
import styles from "./SearchResults.module.css";
import { Spinner } from "../../Spinner/Spinner";

export function SearchResults(props) {
  const { history } = useReactRouter();
  let searchResults = <Spinner />;

  function searchWithFilter(term, location, price) {
    const urlEncodedTerm = encodeURI(term);
    const urlEncodedLocation = encodeURI(location);
    const urlEncodedPrice = encodeURI(price);
    history.push(
      `/search?find_desc=${urlEncodedTerm}&find_loc=${urlEncodedLocation}&price=${urlEncodedPrice}`
    );
  }

  if (props.businesses && props.businesses.length) {
    searchResults = props.businesses.map((b) => (
      <SearchResult
        key={b.id}
        business={b}
        searchWithFilter={searchWithFilter}
      />
    ));
  }

  return <div className={styles["search-results"]}>{searchResults}</div>;
}
