import React, { useState } from "react";
//import { connect } from "react-redux";
import styles from "./SearchResultsSummary.module.css";

export function SearchResultsSummary(props) {
  const [price, setPrice] = useState("");
  const [term, setTerm] = useState(props.term || "");
  const [location, setLocation] = useState(props.location || "");

  function submitFilter(e) {
    if (e.target.value === "1") {
      setPrice = "$";
      props.searchWithFilter(term, location, price);
    } else if (e.target.value === "2") {
      setPrice = "$$";
      props.searchWithFilter(term, location, price);
    } else if (e.target.value === "3") {
      setPrice = "$$$";
      props.searchWithFilter(term, location, price);
    } else if (e.target.value === "4") {
      setPrice = "$$$$";
      props.searchWithFilter(term, location, price);
    } else {
      return;
    }
  }
  let resultStats = null;
  if (props.amountResults && props.shownResults) {
    resultStats = (
      <p>
        Showing 1-{props.shownResults} out of {props.amountResults} results{" "}
      </p>
    );
  }

  return (
    <div className={styles.container}>
      <div className={styles["search-summary"]}>
        <h1 className="subtitle">
          <strong>{props.term}</strong> {props.location}
        </h1>
        {resultStats}
      </div>

      <div className={styles.filters}>
        <button className="button">
          <span className="icon">
            <i className="fas fa-sliders-h"></i>
          </span>
          <span>All filters</span>
        </button>

        <div className="buttons has-addons">
          <button
            className="button"
            onClick={(e) => handlePriceFilter(e)}
            value="1"
          >
            $
          </button>
          <button
            className="button"
            onClick={(e) => handlePriceFilter(e)}
            value="2"
          >
            $$
          </button>
          <button
            className="button"
            onClick={(e) => handlePriceFilter(e)}
            value="3"
          >
            $$$
          </button>
          <button
            className="button"
            onClick={(e) => handlePriceFilter(e)}
            value="4"
          >
            $$$$
          </button>
        </div>

        <button className="button">
          <span className="icon">
            <i className="far fa-times-circle"></i>
          </span>
          <span>Clear filters</span>
        </button>

        {/* <button className='button'>
                    <span className='icon'><i className='fas fa-dollar-sign'></i></span>
                    <span>Cashback</span>
                </button> */}
      </div>
    </div>
  );
}
